
export const a = 10;
export function sum(a, b) { return a + b }

function subtract(a, b) {
    return a - b;
}

export default subtract;

/**
 * 2 ways of exports 
 * 
 * 1. named export => we can make any number of named exports
 * 2. default export => we can make only one default export from a module.
 */